import { InputPicker } from 'rsuite';
import './styles.less';

const SelectInputSearch = (props) => {
    const { inputOptions, noLabel, label } = props;
    return (
        <div
            className={
                `form-input-group
                ${props.inlineLabel ? ' label-inline' : ''}
                ${props.stackedLabel ? ' label-stacked' : ''}
                ${typeof props.stackedLabel === 'undefined' && typeof props.inlineLabel === 'undefined' ? ' label-inline' : ''}`
            }
        >
            {!noLabel && <label>{label} {(props.isMandatory == "true" || props.isMandatory == true) && <span style={{color: "#F62626"}}>*</span>}</label>}
            <InputPicker creatable size="lg" {...inputOptions} />
        </div>
    );
};

export default SelectInputSearch;
